/* jshint esversion: 6 */
import React, { useState, useEffect } from "react";
import "./App.css";

import Dashboard from "./Dashboard"
import LoginScreen from "./LoginScreen";
import TrophyCase from "./TrophyCase";


import blankImg          from "./assets/images/blank.jpg";
import pushUpImg         from "./assets/images/push-up.jpg";
import squatImg          from "./assets/images/squat.jpg";
import benchPressImg     from "./assets/images/bench-press.jpg";
import deadliftImg       from "./assets/images/deadlift.jpg";
import overheadPressImg  from "./assets/images/overhead-press.jpg";
import pullUpImg         from "./assets/images/pull-up.jpg";
import lungeImg          from "./assets/images/lunge.jpg";
import plankImg          from "./assets/images/plank.jpg";
import bicepCurlImg      from "./assets/images/bicep-curl.jpg";
import tricepDipImg      from "./assets/images/tricep-dip.jpg";

function ExerciseSelector({ onSelect }) {
  const exercises = [
    "Push-Up",
    "Squat",
    "Bench Press",
    "Deadlift",
    "Overhead Press",
    "Pull-Up",
    "Lunge",
    "Plank",
    "Bicep Curl",
    "Tricep Dip",
  ];
  const [chosen, setChosen] = useState("");

  return (
    <div className="selector-container">
      <label htmlFor="exercise" className="selector-label">
        Choose an exercise:
      </label>
      <select
        id="exercise"
        value={chosen}
        onChange={(e) => setChosen(e.target.value)}
        className="exercise-select"
      >
        <option value="">-- none --</option>
        {exercises.map((ex) => (
          <option key={ex} value={ex}>
            {ex}
          </option>
        ))}
      </select>
      <button
        className="select-button"
        onClick={() => onSelect(chosen)}
      >
        Select Exercise
      </button>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [view, setView] = useState("workout");

  const [selectedExercise, setSelectedExercise] = useState("");
  const [reps, setReps] = useState("");
  const [notes, setNotes] = useState("");
  const [workoutId, setWorkoutId] = useState(null);

  const exerciseImages = {
    "Push-Up": pushUpImg,
    Squat: squatImg,
    "Bench Press": benchPressImg,
    Deadlift: deadliftImg,
    "Overhead Press": overheadPressImg,
    "Pull-Up": pullUpImg,
    Lunge: lungeImg,
    Plank: plankImg,
    "Bicep Curl": bicepCurlImg,
    "Tricep Dip": tricepDipImg,
  };

    const handleExercise = exercise => {
      const img = exerciseImages[exercise];
      if (exercise && img) {
        console.log(`Selected exercise: ${exercise}`);
        const filename = img.split("/").pop();
        console.log(`Image displayed: ${filename}`);
        console.log("Inputs visible: Reps field, Notes textarea");
      } else {
        console.error("FAIL");
      }
      setSelectedExercise(exercise);
      setReps("");
      setNotes("");
  };

  const currentImage =
    selectedExercise && exerciseImages[selectedExercise]
      ? exerciseImages[selectedExercise]
      : blankImg;

  useEffect(() => {
    console.log("ðŸ“¡ GET /api/workouts: fetching without headers");

    fetch("/api/workouts")
        .then((res) => res.json())
        .then((data) => {
          if (data.length) {
            setWorkoutId(data[data.length - 1].id);
          }

          else {
            return fetch("/api/workouts", {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ user_id: 1 })
            })
                .then((res) => res.json())
                .then((created) => setWorkoutId(created.workout_id));
          }
        })
        .catch(console.error);
    }, []);


  const handleSave = async () => {
    try {
      const payload = {
        workout_id: workoutId,
        name: selectedExercise,
        sets: 1,
        reps: parseInt(reps, 10),
        weight: null,
        notes,
      };

      const res = await fetch("/api/exercises", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(text || res.statusText);
      }

      const { message } = await res.json();
      alert(message);

      // Reset UI
      setSelectedExercise("");
      setReps("");
      setNotes("");
    } catch (err) {
      console.error(err);
      alert("ERROR: " + err.message);
    }
  };

if (!isLoggedIn) {
  return (
    <div className="App">
      <h1>Fitness App</h1>
      <LoginScreen
        onLogin={async (email, password) => {
          try {
            const res = await fetch("/api/login", {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ email, password })
            });
            const data = await res.json();
            if (!res.ok) throw new Error(data.message || res.statusText);
            setIsLoggedIn(true);
          } catch (err) {
            alert("Login failed: " + err.message);
          }
        }}
        onSwitchToSignup={async (email, password) => {
          try {
            const res = await fetch("/api/register", {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ email, password })
            });
            const data = await res.json();
            if (!res.ok) throw new Error(data.message || res.statusText);
            setIsLoggedIn(true);
          } catch (err) {
            alert("Signup failed: " + err.message);
          }
        }}
      />
    </div>
  );
}

  return (
    <div className="App">
      <h1>Fitness App</h1>
      <div className="nav-bar">
        <button onClick={() => setView("workout")}>Workout</button>
        <button onClick={() => setView("trophies")}>Trophies</button>
        <button onClick={() => setView("dashboard")}>Dashboard</button>
      </div>
      {view === "workout" && (
        <>
          <h2>Exercise Log</h2>
          <ExerciseSelector onSelect={handleExercise} />
          <div className="exercise-image-container">
            <img src={currentImage} alt={selectedExercise || "No exercise selected"} className="exercise-image" />
          </div>
          {selectedExercise && (
            <>
              <p className="selected-display">Selected exercise: <strong>{selectedExercise}</strong></p>
              <div className="input-group">
                <label htmlFor="reps">Reps:</label>
                <input id="reps" type="number" min="1" value={reps} onChange={e => setReps(e.target.value)} />
              </div>
              <div className="input-group">
                <label htmlFor="notes">Notes:</label>
                <textarea id="notes" rows="3" value={notes} onChange={e => setNotes(e.target.value)} placeholder="How did it feel?" />
              </div>
              <button className="save-button" onClick={handleSave} disabled={!selectedExercise || !reps || workoutId === null}>Save Entry</button>
            </>
          )}
        </>
      )}
      {view === "trophies" && (
        <>
          <h2>Trophy Case</h2>
          <TrophyCase />
        </>
      )}
      {view === "dashboard" && (
        <>
          <h2>Dashboard</h2>
          <Dashboard userId={1} />
        </>
      )}
    </div>
  );
}

export default App;
